const e=JSON.parse('{"key":"v-7d7a976e","path":"/bookList/Electrical%20Engineering%20101/five.html","title":"电路可视化让电路变得简单","lang":"zh-CN","frontmatter":{"title":"电路可视化让电路变得简单","icon":"fab fa-markdown","order":5,"category":["硬件"],"tag":["历史"],"next":"six","prev":"four"},"headers":[{"level":2,"title":"#1把所有不可视的都可视化","slug":"_1把所有不可视的都可视化","link":"#_1把所有不可视的都可视化","children":[{"level":3,"title":"1.1   |万用表","slug":"_1-1-万用表","link":"#_1-1-万用表","children":[]},{"level":3,"title":"1.2   |示波器","slug":"_1-2-示波器","link":"#_1-2-示波器","children":[]},{"level":3,"title":"1.3   |逻辑分析仪","slug":"_1-3-逻辑分析仪","link":"#_1-3-逻辑分析仪","children":[]},{"level":3,"title":"1.4   |仿真与现实","slug":"_1-4-仿真与现实","link":"#_1-4-仿真与现实","children":[]},{"level":3,"title":"1.5   |电烙铁","slug":"_1-5-电烙铁","link":"#_1-5-电烙铁","children":[]},{"level":3,"title":"1.6   |焊接的一些相关事情，","slug":"_1-6-焊接的一些相关事情","link":"#_1-6-焊接的一些相关事情","children":[]},{"level":3,"title":"1.7   |拆锡","slug":"_1-7-拆锡","link":"#_1-7-拆锡","children":[]},{"level":3,"title":"1.6   |那个年代的职业，每个职业的帮助","slug":"_1-6-那个年代的职业-每个职业的帮助","link":"#_1-6-那个年代的职业-每个职业的帮助","children":[]}]}],"git":{"createdTime":1700894670000,"updatedTime":1700894670000,"contributors":[{"name":"unknown","email":"https://github.com/839240613","commits":1}]},"readingTime":{"minutes":4.79,"words":1438},"filePathRelative":"bookList/Electrical Engineering 101/five.md","localizedDate":"2023年11月25日","excerpt":"<h2> 1把所有不可视的都可视化</h2>\\n\\n<p>电子的流动你看不见，但是能够看见灯泡电流产生的光和热。</p>\\n<p>所以需要测量电路的各种电气属性，把看不见的都可视化，才可以了解到电路更多的消息。</p>\\n<p>但是无论测量什么电路，在开启电源之前，应该设置好示波器，尽可能的估算好被测信号的大小。</p>\\n<p>这样才能不捕捉到自己想要的东西，自动并不能很好的实现你的需求。</p>\\n<h3> 1.1 &nbsp; 万用表</h3>\\n<p>输入阻抗就是相关整个电路戴维南之后和示波器耦合的电阻。</p>\\n<p>根据前人的结论，一般测电压，需要电路电阻和示波器的比值约为1:100才能够实现，</p>"}');export{e as data};
